import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "../NewExpense/ExpensesFilter";
import { useState } from "react";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const onSelectedFilterHandler = (filterValue) => {
    setFilteredYear(filterValue);
  };

  const filteredData = props.expenses.filter((item) => {
    return item.date.getFullYear() == filteredYear;
  });

  let expressContent = <p>No Expenses found.</p>;

  if (filteredData.length > 0) {
    expressContent = filteredData.map((expense, index) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }
  return (
    <div>
      <Card className="expenses ">
        <ExpensesFilter
          selected={filteredYear}
          onSelectFilter={onSelectedFilterHandler}
        />
        {expressContent}
      </Card>
    </div>
  );
};

export default Expenses;
