              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
    ));


  }

  return (
    <div>
      <Card className="expenses ">
        <ExpensesFilter
          selected={filteredYear}
          onSelectFilter={onSelectedFilterHandler}
        />
        {filteredData.length === 0 ? (
          <p>No Expenses Found</p>
        ) : (
          filteredData.map((expense, index) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          ))
        )};
      </Card>
    </div>
  );
};

export default Expenses;
